%Runge Kutta Method for systems%Input the functions, the initial conditions and the final (xf)%Output the plot of the functionyp=@(x,y,z) x+y*z; zp=@(x,y,z) y+x*z;x0=0; y0=1;z0 = -1; xs=[x0]; ys=[y0]; zs=[z0]; h=0.05; xf= 2; a= (xf-x0)/h; for i=1:a   k11=h*yp(xs(end),ys(end),zs(end));   k12=h*zp(xs(end),ys(end),zs(end));  k21=h*yp(xs(end)+h/2,ys(end)+k11/2,zs(end)+k12/2);  k22=h*zp(xs(end)+h/2,ys(end)+k11/2,zs(end)+k12/2);  k31=h*yp(xs(end)+h/2,ys(end)+k21/2,zs(end)+k22/2);   k32=h*zp(xs(end)+h/2,ys(end)+k21/2,zs(end)+k22/2);   k41=h*yp(xs(end)+h,ys(end)+k31,zs(end)+k32);  k42=h*zp(xs(end)+h,ys(end)+k31,zs(end)+k32);  deltay=(1/6)*(k11+2*k21+2*k31+k41);  deltaz=(1/6)*(k12+2*k22+2*k32+k42);  ys(end+1)=ys(end)+deltay;   zs(end+1)=zs(end)+deltaz;  xs(end+1)=xs(end)+h; endplot(xs,ys,xs,zs)